/* Miniatura do vídeo */
        #thumbnail {
            position: fixed;
            bottom: 60%;
            left: 5%;
            width: 80px;
            height: 80px;
            border-radius: 50%;
            background-size: cover;
            background-color: #ffe761;
            background-position: center;
            overflow: hidden;
            display: flex;
            justify-content: center;
            align-items: center;
            z-index: 9999;
            border: 5px solid transparent;
            box-shadow: 0 4px 10px rgba(0, 0, 0, 0.7);
            cursor: pointer;
            animation: borderBlink 1s infinite;
        }

        #thumbnail p {
            color: black;
            font-size: 12px;
            text-align: center;
            animation: textFade 1.5s infinite alternate;
            position: absolute;
        }

        @keyframes borderBlink {
            0% { border-color: transparent; }
            50% { border-color: #000; }
            100% { border-color: transparent; }
        }

        @keyframes textFade {
            0% { opacity: 0; }
            100% { opacity: 1; }
        }

        /* Container do vídeo em formato stories */
        #video-container {
            display: none;
            position: fixed;
            top: 50%;
            left: 50%;
            width: 360px;
            height: 640px;
            transform: translate(-50%, -50%);
            background-color: #000;
            z-index: 10000;
            border-radius: 20px;
            box-shadow: 0 8px 20px rgba(0, 0, 0, 0.7);
            display: flex;
            flex-direction: column;
            align-items: center;
            overflow: hidden;
        }

        #video-container iframe {
            width: 100%;
            height: 100%;
            border: none;
        }

        /* Botão de fechar em destaque */
        #close-btn {
            position: absolute;
            top: 10px;
            right: 10px;
            background-color: rgba(255, 0, 0, 0.8);
            color: white;
            border: none;
            font-size: 16px;
            padding: 8px 12px;
            cursor: pointer;
            z-index: 10001;
            border-radius: 5px;
        }

        /* Botões de navegação para mudar o vídeo */
        .nav-btn {
            position: absolute;
            top: 50%;
            transform: translateY(-50%);
            background-color: rgba(0, 0, 0, 0.5);
            color: white;
            font-size: 30px;
            border: none;
            padding: 10px;
            cursor: pointer;
            z-index: 10001;
        }

        .prev-btn {
            left: 10px;
        }

        .next-btn {
            right: 10px;
        }
